
public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1 = 2;
		int n2 = 4;
		
		System.out.println("El primer valor numerico es de " + n1);
		System.out.println("El segundo valor numerico es de " + n2);
		if (n1 > n2) {
			System.out.println("El primer valor numerico es mayor al segundo");
		} else if (n1 == n2) {
			System.out.println("El primer y segundo valor numericos son iguales");
		}else {
			System.out.println("El segundo valor numerico es mayor al primero");
		}

	}

}
